package br.com.carlosFreitas.tiposPrimitivos;

public class Literais {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double d = 2345678d; // d - especificando o tipo, e double pode ser anotado em notação cientifica
		float v = 1.45f;
		//	        separando valores com _ underscor, cpf, cartao, pi, binarios;
		//	        sufixos l - long, f - float
		//	        prefixos 0x5;

		System.out.println("Valor Double: "
				+ d + "\nValor Float: " + v);
	}

}
